component.custom-start-script:
import sys; print('Python %s on %s' % (sys.version, sys.platform)); sys.path.extend([WORKING_DIR_AND_PYTHON_PATHS])
from flask.cli import ScriptInfo, NoAppException
for module in ["main.py", "wsgi.py", "app.py"]:
    try: locals().update(ScriptInfo(app_import_path=module, create_app=None).load_app().make_shell_context()); print("\nFlask App: %s" % app.import_name); break
    except NoAppException: pass

